import React from 'react';

interface IProduct
{
	sno:string;
	name:string;
	image:string;
	price:number;
	qty:number;
}

interface IProps
{
	
}
interface IState
{
	product:IProduct;
}

class Product extends React.Component<IProps,IState>
{
	constructor(props:IProps)
	{
		super(props);

		this.state={
						product:{
									sno:'AA101',
									name:'Apple Watch',
									image:'https://images-na.ssl-images-amazon.com/images/I/71fwbMm1NBL._AC_SL1500_.jpg',
									price:46000,
									qty:2
								}
				   }
		
	}

	//incQty Function

	incQty=()=>
	{
		this.setState( {
						product:
						{
							...this.state.product,
							qty:this.state.product.qty+1	
						}
					   }
					 )
	}

	//decQty Function

	decQty=()=>
	{
		this.setState(
						{
							product:
							{
								...this.state.product,
								qty:(this.state.product.qty-1>0)?this.state.product.qty-1:1
							}
						}
			         )
	}

	render()//It is an instance method
	{
		let {product}=this.state;

		return( 
				<React.Fragment>
					<section className="mt-3">
						<div className="container">
							<div className="row">
								<div className="col">
									<h2 className="text-primary">Product Item</h2>
									<p>Incididunt mollit nisi occaecat laborum laborum qui id labore consequat aute do sed cillum reprehenderit ex minim. Nisi labore sit laborum ad laborum aute ut aliqua esse sunt consequat aliquip esse. Adipisicing cupidatat consectetur est amet labore adipisicing mollit irure ex cillum veniam laboris laboris labore.</p>
								</div>
							</div>
						</div>
					</section>

					<section className="mt-3">
						<div className="container">
							<div className="row">
								<div className="col">
									<table className="table table-hover text-center table-striped table-warning">
										<thead>
											<tr>
												<th>SNO</th>
												<th>Image</th>
												<th>Name</th>
												<th>Price</th>
												<th>QTY</th>
												<th>Total</th>
											</tr>
										</thead>
										<tbody className="bg-light">
											<tr>
												<td>{product.sno}</td>
												<td>
													<img src={product.image} alt="" width="50px" height="50px"/>
												</td>
												<td>{product.name}</td>
												<td>{product.price}</td>
												<td>
													<i className="fa fa-minus-circle mx-2" onClick={this.decQty}/>
													{product.qty}
													<i className="fa fa-plus-circle mx-2" onClick={this.incQty}/>
												</td>
												<td>{product.price*product.qty}</td>
											</tr>
										</tbody>
									</table>
								</div>
							</div>
						</div>
					</section>
				</React.Fragment>

			);
	}
}

export default Product;
