import React from 'react'

interface IProps{

}

interface IState {
    text:string
Count:number
}
class Props extends React.Component<IProps,IState>
{
    constructor(props:IProps)
    {
        super(props)
            this.state={
                text:'',
                Count:100
                
            }
        }
        
 update=(event:React.ChangeEvent<HTMLButtonElement>)=>{
     this.setState(
         {
            text:event.target.value,
            Count:1+event.target.value.length
         }
     )
     
 }
    

render()
{
    return(
        <React.Fragment>
           <div>
            <button onChange={this.update} value='' >
                update
                
            </button>
            </div>
            <h3 >{this.state.text}1</h3>
        </React.Fragment>
    )
}

}
export default Props;
