import React from 'react';


interface IProps
{
	
}
interface IState
{

	text:string
	charCount:number
}

class SmsApp extends React.Component<IProps,IState>
{
	constructor(props:IProps)
	{
		super(props);

		this.state={
			text:'',
			charCount:100
	
		}
		
	}
	updateText=(event:React.ChangeEvent<HTMLTextAreaElement>)=>{
		this.setState(
			{
				text:event.target.value,
				charCount:100-event.target.value.length
			}
		)
	}
		

	render()//It is an instance method
	{
	
		return( 
				<React.Fragment>
					<section className='m-5'>
						<div className='container-mt-5-ml-5'>
							<div className='row'>
								<div className='col-md-5'>
									<div className='card'>
									<div className='card-header bg-success text-white text-center'>
										<h3>Sms App</h3>
									</div>
									<div className='card-body bg-light'>
										<form>
									<textarea className='form-control' rows={4} onChange={this.updateText}></textarea>
									</form>
								
									</div>
									<div className='card-footer bg-success text-white text-left' >
										<h5 >Character remaining:
										<span>{this.state.charCount}</span>
										</h5>
									</div>
									</div>
									</div>
								</div>
							</div>
						
					</section>
		
				</React.Fragment>

			);
	}
}

export default SmsApp;
