import React from 'react'
interface IProps{

}
interface IState{
name:string
}



 class State extends React.Component<IProps,IState>
{
    constructor(props:IProps)
    {
    super(props)
this.state=
    {
        name:''
    }

        }
        update=(event:React.ChangeEvent<HTMLInputElement>)=>
        {
            this.setState(
                {
                    name:event.target.value
                }
            )
        }
    





render()
{
    return(
        <React.Fragment>
    <div className='card'>
        <input type='text' placeholder='name' className='form-control' onChange={this.update}/>

    </div>
    <h3>{this.state.name}</h3>
        </React.Fragment>
    )
}
}
export default State;
