import axios, { AxiosResponse } from 'axios';
import React from 'react';
import {RouteComponentProps} from 'react-router-dom'
import { IEmployee } from '../Models/IEmployee';

interface IProps extends RouteComponentProps<{employeeId:string}>
{
    
}
interface IState
{

selectedemployee:IEmployee | undefined

}
class EmployeeDetails extends React.Component<IProps,IState>
{
    constructor(props:IProps)
    {
        super(props)

    this.state={
        selectedemployee:{} as IEmployee
    }
    }
    
    componentDidMount():void{
        let dataURL="https://gist.githubusercontent.com/ms8989/9beec0762a6aa57decaff89510c6d7c5/raw/df502c330b93d412b6b8038f7b9cfb3898397efd/contact2"
    axios.get(dataURL).then((response:AxiosResponse<any>)=>{

    let employees:IEmployee[]= response.data
    let singleEmployee:IEmployee | undefined=employees.find((employee)=>
    {
        return employee.login.uuid === this.props.match.params.employeeId
    })
this.setState(
    {
        selectedemployee:singleEmployee

    }
)

    }).catch((error)=>{
        console.log(error)
    })
    }
    
        
    render()
    {
        return(
<React.Fragment>
<section className='mt-3'>
    <div className='container'>
        <div className='row'>
            <div className='col-md-7'>
                <div className='card'>
                    <div className='card-header'>
                        <h3 className='text-cnter text-primaryy foont-weight-bold'>
                            
                            Employee Details....</h3>
                        <div className='card-body'>
                           {/* <pre>{JSON.stringify(this.props.match.params.employeeId)}</pre>*/}
                           <pre>{JSON.stringify(this.state.selectedemployee)}</pre>

                        </div>
                    </div>
                </div>
            </div>
        </div>
    </div>

</section>
 
</React.Fragment>
        )
    }
};
export default EmployeeDetails;
