import axios, { AxiosResponse } from 'axios';
import React from 'react';
import { NavLink } from 'react-router-dom';
import { IEmployee } from '../Models/IEmployee';

interface IProps{

}
interface IState{
employees:IEmployee[]
errormessage:string;

}
class Employee extends React.Component<IProps,IState>
{
    constructor(props:IProps)
    {
        super(props)
        this.state={
            errormessage:'',
            employees:[] as IEmployee[]
        }

    } 
componentDidMount():void
{
    let dataURL:string="https://gist.githubusercontent.com/ms8989/9beec0762a6aa57decaff89510c6d7c5/raw/df502c330b93d412b6b8038f7b9cfb3898397efd/contact2"
    axios.get(dataURL).then((response:AxiosResponse<any>)=>
    {
       this.setState(
           {
               ...this.state,
               employees:response.data
           }
       ) 
    }).catch((error)=>
    {
        this.setState(
            {
                ...this.state,
                errormessage:error.message

        })
        
    })
}        
    
    render()
   
    {
        let {employees}=this.state;
        return(
<React.Fragment>
<section>
 <div className='container mt-2'>
     <div className='row'>
         <div className='col'>
             <div className='card'>
                 <div className='card-body bg-light'>
             <h2 className='text-primary text-center' >Employees</h2>
             <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Enim officiis illum praesentium culpa aperiam doloremque omnis corporis quas, voluptates in modi vel totam ipsum eius tempora ratione laboriosam perferendis amet id beatae accusamus, mollitia quod debitis. Harum numquam repellat earum?</p>
         </div>
         </div>
         </div>
     </div>
 </div>
 </section>
 <section>
     <div className='container mt-5'>
         <div className='row'>
             <div className='col'>
                 {/* <pre>{JSON.stringify(this.state.employees)}</pre> */}
                 
                <table className='table  table-hover text-center text-white'>
                      <thead className='font-weight-bold text-center bg-white text-dark'>
                      <tr>
                        <th>S.No</th>
                        <th>Name</th>
                        <th>Age</th>
                        <th>Email</th>
                        <th>ConTactNo.</th>
                        <th>Location</th>
                      </tr>
                       </thead>
                       <tbody>
                            {
                           employees.length>0 &&
                           employees.map((employee)=>{
                               return(
                                   <tr>
                                        <td>{employee.login.uuid.substr(employee.login.uuid.length-5)}</td>
                                       <td> <NavLink to={`/employees/${employee.login.uuid}`} className='text-primary'>{employee.name.title}{employee.name.first}{employee.name.last}</NavLink></td>
                                      <td>{employee.dob.age}</td>
                                       <td>{employee.email}</td>
                                       <td>{employee.phone}</td>
                                       <td>{employee.location.city}</td>
                                       
                                   </tr>
                               )
                           })
                            }                          
                       </tbody>
                 </table>
             </div>
         </div>
     </div>
 </section>
</React.Fragment>
        )
    }
};
export default Employee;
