import axios, { AxiosResponse } from 'axios';
import React from 'react';
import {RouteComponentProps} from 'react-router-dom'
import { IStock } from '../Models/IStock';

interface IProps extends RouteComponentProps<{stocksId:string}>
{

}
interface IState{

selectedstock:IStock | undefined
}
class StockDetails extends React.Component<IProps,IState>
{
    constructor(props:IProps)
    {
        super(props)
        this.state={
            selectedstock:{} as IStock
        }

    }   
        componentDidMount():void
        {
            let dataURL:string="https://gist.githubusercontent.com/ms8989/b06fcbf8b090bcd9b871abae74ee64e2/raw/4b22230e511a17c343158e41e88668d18293e3c2/stock2"
        axios.get(dataURL).then((response:AxiosResponse<any>)=>{
            let stocks:IStock[]=response.data
            let singleStock=stocks.find((stock)=>{
                return stock.Stockid == this.props.match.params.stocksId

            })
            this.setState(
                {
                    selectedstock:singleStock

                }
            )
        }).catch((error)=>{
            console.log(error)
        })
        }

    

    
    render()
    {
        let {selectedstock}=this.state
        return(
<React.Fragment>
<section mt-2>
    <div  className='container'>
        <div className='row'>
            <div className='col'>
                <div className='card'>
                <div className='card-header bg-light'>
                <h3 className='text-primary'>
                    StocksDetails
                </h3>
                <p>Lorem ipsum dolor sit amet, consectetur adipisicing elit. Ipsa velit quo dignissimos, quia explicabo nisi veniam eum voluptas incidunt mollitia.</p>
            </div>
        </div>
    </div>
</div>
</div>
</section>

 

<section mt-3>
{
selectedstock!==undefined &&
Object.keys(selectedstock).length>0 ?
<React.Fragment>
    <div className='container mt-4 ml-6'>
    <div className='row'>
        <div className='col-md-7'>
            <div className='card'>
                <div className='card-header'>
                    <h3>
                        {selectedstock.StockName}
                    </h3>
                </div>
                <table className='table table-striped table-hover table-primary text-white bg-dark'>
                <thead className='bg-dark text-primary'>
                      <tr>
                        <th>StockId</th>
                        <th>StockName</th>
                        <th>StockIndustry</th>
                        <th>Symbol</th>
                        <th>Currency</th>
                        <th>StockMarket</th>
                      </tr>
                       </thead>
                </table>
            </div>
        </div>
    </div>
    </div>
</React.Fragment>:null
    }
</section>
    
 
</React.Fragment>
        )
    }
};
export default StockDetails;
