                        {/*<pre>{JSON.stringify(this.props.match.params.employeeId)}</pre>*/}
                          
                        
                    </div>
                </div>
            </div>
        </div>
    </div>

</section>
<section className='mt-3'>
{/*<pre>{JSON.stringify(this.state.selectedemployee)}</pre>*/}
                             {
                                 selectedemployee!==undefined &&
                                 Object.keys(selectedemployee).length>0 ?
                                <React.Fragment>
                                    <div className='container'>
                                        <div className='row'>
                                            <div className='col md-7'>
                                                <div className='card bg-primary'>
                                                    <h3 className='card-header text-primary bg-dark'>
                                                        {selectedemployee.name.first}{selectedemployee.name.last}
                                                    </h3>
                                                </div>
                                                <div className='card-boy'></div>
                                                <div className='row'>
                                                    <div className='col-md-3'>
                                                        
                                                            <img src={selectedemployee.picture.large} className='img-fluid'/>
                                                        </div>
                                                        <div className='col-md-7'>
                                                            <ul
                                                             className='list-group'>
                                                                <li className='list-group-item'>
                                                                    Age:{selectedemployee.dob.age}
                                                                </li>   
                                                                <li className='list-group-item'>
                                                                    Age:{selectedemployee.dob.age}
