                                                                    Age:{selectedemployee.dob.age}
                                                                </li>   <li className='list-group-item'>
                                                                    Age:{selectedemployee.dob.age}
                                                                </li>   <li className='list-group-item'>
                                                                    Age:{selectedemployee.dob.age}
                                                                </li>   
                                                                        </ul>
                                                                
                                                            
                                                        </div>
                                                    </div>
                                                    </div>                                                

                                                </div>
                                            </div>
                                        

                                    
                                </React.Fragment>:null
                        }
</section>
 
</React.Fragment>
        )
    }
};
