import React from 'react'

interface IPrps{

}
interface IState{
    
message:string
}
class State extends React.Component<IPrps,IState>
{
    constructor(props:IPrps)
    {
    super(props)
    this.state=
        {
            message:''
        }
    

}
update=()=>{
    this.setState(
        {
            message:'bruce'
        }
    )
}


render()
{
    return(
        <React.Fragment>
<h3 >{this.state.message}Corona</h3>
<button onClick={this.update}>update</button>
        </React.Fragment>
    )
}
}
export default State;
