import React from 'react'

interface IProps{

}
interface IState{
text:string;
Charcount:number;
}
class State_2 extends React.Component<IProps,IState>
{
    constructor(props:IProps){
        super(props)
        this.state={
            text:'',
            Charcount:100
        }
    }
    update=(event:React.ChangeEvent<HTMLTextAreaElement>)=>{
        this.setState(
            {
                text:event.target.value,
                Charcount:length-100-event.target.value.length 
            }
        )
    }
    

render()
{
    return(
        <React.Fragment>
<div className='card'>
    <textarea rows={5} onChange={this.update}>

    </textarea>
    <small>{this.state.Charcount}CharcterRemaining</small>
</div>
        </React.Fragment>
    )
}

}
export default State_2
