import React from 'react'
import {IUser} from './Iuser'
interface IProps{

}
interface IState{
	user:IUser;

}
class Register extends React.Component<IProps,IState>
{
    constructor(props:IProps)
    {
        super(props)
		this.state={
			user:{
				UserName:'',
				Email:'',
				password:'',
				designation:'',
				terms:false
			}
		}
      
        
    }
	//updateInput
	updateInput=(event:React.ChangeEvent<HTMLInputElement|HTMLSelectElement>)=>{
		this.setState(
			{
				user:{
					...this.state.user,
					[event.target.name]:event.target.value
			}
	});


	}
	//updateCheck
	updateCheck=(event:React.ChangeEvent<HTMLInputElement>)=>{
		this.setState(
			{
				user:{
					...this.state.user,
					[event.target.name]:event.target.checked
					
				}
			});
	}	
	submitRegister=(event:React.FormEvent<HTMLElement>)=>{
		event.preventDefault();
		console.log(this.state.user);
	}
    
    render()
    {
	
		let {user}=this.state
        return(
<React.Fragment>
	<pre>{JSON.stringify(this.state.user)}</pre>
   <section>
	   <div className='mt-5'>
		   <div className='row'>
			   <div className='col-md-5'>
				   <div className='card'>
					   <div className='card-header bg-secondary'>
						   <h3>Register Form</h3>
					   </div>
					   <div className='card-body bg-light'>
						   <form>
							   <div className='form-group mb-2'>
							   <input type='text' placeholder='Enter UserName' className='form-control'onChange={this.updateInput} value={user.UserName} name='UserName'/></div>
							   <div className='form-group mb-2'>
							   <input type='text' placeholder='Enter email' className='form-control'onChange={this.updateInput} value={user.Email} name='Email'/></div>
							   <div className='form-group mb-2'>
							   <input type='password' placeholder='Enter password' className='form-control'onChange={this.updateInput} value={user.password} name='password'/></div>
							   <div className='form-group mb-2'>
							   <select className='form-control'onChange={this.updateInput} value={user.designation} name='designation'>
								   <option>.......Select Designation.......</option>
								   <option>Team lead</option>
								   <option>Manager</option>
								   <option>Developer</option>
							   </select>
							   </div>
							   <div className='form-group mb-2'>
							   <input type='checkbox' onChange={this.updateCheck} value='' name='terms'/>Accept terms</div>
							   <div className='form-group mb-2'>
							   <input type='submit'className='bg-secondary' onSubmit={this.submitRegister}/></div>
						   </form>
					   </div>
				   </div>
			   </div>
		   </div>

	   </div>
   </section>
</React.Fragment>
        )
    }
};
export default Register;
