import React from 'react'
import {IContact} from './IContact'
import ContactList from './ContactList'
import ContactCard from './ContactCard'
interface IProps{

}
interface IState{

recieveData:IContact
}
class Contactapp extends React.Component<IProps,IState>
{

    constructor(props:IProps)
    {
        super(props);
        this.state={
            recieveData:{} as IContact
        }
        
        
    }
    //recieveData
    recieveData=(value:IContact)=>
    {
        this.setState({
            recieveData:value
        })
    }

    

render()
{
    
return(

<React.Fragment>
<section className='mt-3'>
<div className='container'>
    <div className='row'>
    <div className='col'>
        <h2>Contact App</h2>
        <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Temporibus molestiae commodi nam laborum labore facilis maxime mollitia asperiores ab quis sapiente, inventore modi aut corrupti aliquid dolore, architecto, quas nihil?</p>
    </div>
     </div>
     <div className='row'>
         <div className='col-md-9'>
             <ContactList sendData={this.recieveData}/>
         </div>
         <div className='col-md-3'>
             {
                 Object.keys(this.state.recieveData).length>0 &&

                 <ContactCard sendItem={this.state.recieveData}/>
             }
         </div>
     </div>
     </div>
</section>

</React.Fragment>
)
}
}
export default Contactapp
