import React from 'react';

interface IProps{

}
interface IState{
userName:string;
}
class UserInput extends React.Component<IProps,IState>
{
    constructor(props:IProps)
    {
        super(props)
    this.state={
        userName:''
    }   
        
    }
    changeInput=(event:React.ChangeEvent<HTMLInputElement>)=>
    {
        this.setState(
            {
             userName:event.target.value   
            }
        )
    }

    
    render()
    {
        return(
<React.Fragment>
<section>
    <div className='container mt-5 ml-5'>
        <div className='row'>
            <div className='col-mt-3'>
                <div className='card'>
                    <div className='card-header'>
                        <h3>Change username</h3>
                    </div>
                    <div className='card-body'>
                        <form>
                        <div className="mb-3">
                        
  
  <input type="text" className="form-control" placeholder="EnterUserName" onChange={this.changeInput}>
</input>
  </div>
                        </form>
                        <h4 className='text-primary'
                        >{this.state.userName}</h4>
                        </div>
                        </div>
                        </div>
                        
                        
    
</div>
</div>
    </section>
</React.Fragment>
        )
    }
};
export default UserInput;
